import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button } from '@material-ui/core';

const App = () => {
  const [attendance, setAttendance] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/attendanceRecord');
        setAttendance(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    fetchAttendance();
  }, []);

  const handleEdit = (id) => {
    // Implement edit functionality
    console.log(`Editing attendance record with ID ${id}`);
  };

  return (
    <div>
      <h1>Attendance Records</h1>
      <table>
        <thead>
          <tr>
            <th>User ID</th>
            <th>User Name</th>
            <th>Date</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map(record => (
            <tr key={record._id}>
              <td>{record.userId}</td>
              <td>{record.userName}</td> {/* Assuming userName is available in the data */}
              <td>{record.date}</td>
              <td>{record.status}</td>
              <td>
                <button onClick={() => handleEdit(record._id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
