import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal } from '@mui/material';


const App = () => {
  const [attendance, setAttendance] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [open, setOpen] = useState(false);

  const handleEdit = (id) => {
    setSelectedId(id);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/attendanceRecord');
        setAttendance(response.data);
      } catch (error) {
        console.error('Error fetching attendance records:', error);
      }
    };

    fetchAttendance();
  }, []);

//   const handleEdit = (id) => {
//     // Implement edit functionality
//     console.log(`Editing attendance record with ID ${id}`);
//   };

  return (
    <div>
      <h1>Attendance Records</h1>
      <table>
        <thead>
          <tr>
            <th>User ID</th>
            <th>Date</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map(record => (
            <tr key={record._id}>
              <td>{record.userId}</td>
              <td>{record.date}</td>
              <td>{record.status}</td>
              <td>
                <button onClick={() => handleEdit(record._id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal open={open} onClose={handleClose}>
        <div>
          <h2>Set Attendance Status</h2>
          <div>
          <h2>Set Attendance Status</h2>
          <Select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          >
            <MenuItem value="present">Present</MenuItem>
            <MenuItem value="absent">Absent</MenuItem>
          </Select>
          <Button onClick={handleSetStatus}>Save</Button>
        </div>
        </div>
      </Modal>
    </div>
  );
};

export default App;
