import axios from 'axios';
import React from 'react';
import Calendar from 'react-calendar';
import { Route } from 'react-router-dom';

function CalendarPage() {

    const markAttendance = async (date) => {
        console.log(date);
      
        // Convert the JavaScript Date object to a string in ISO 8601 format
        const isoDateString = date.toISOString();
      
        try {
          await axios.post('http://localhost:5000/api//mark', { date: isoDateString, status: 'present' });
          alert('Attendance marked successfully');
        } catch (error) {
          console.error('Error marking attendance:', error);
          alert('Failed to mark attendance');
        }
      };
  return (
    <div>
      <h2>Attendance Calendar</h2>
    

      
      < Calendar onClickDay={markAttendance} />
     
    </div>
  );
}

export default CalendarPage;
