import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Header() {
  const [user, setUser] = useState(null);


    const fetchUserDetails = async () => {
      
        const token = localStorage.getItem('token');
        console.log(token);
        if (!token) {
            console.error('No authentication token found');
            return;
        }
        try {
        
        const response = await axios.get('http://localhost:5000/api/user', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        console.log("cc",response);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    useEffect(() => {

    fetchUserDetails();
  }, []);

  const handleLogout = () => {
    // Perform logout logic (clear localStorage, reset state, etc.)
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/login'; 
    
    // Additional logout logic as needed
  };

  return (
    <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' ,backgroundColor:"lightgray"}}>
      <div className="logo">MY LOGO</div>
      {user ? (
        <div className="user-info" style={{ display: 'flex', alignItems: 'center' }}>
          <span style={{ fontWeight: 'bold', marginRight: '10px' }}>Welcome, {user.username}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div className="login-links">
          {/* Your login/register links/buttons */}
        </div>
      )}
    </header>
  );
}

export default Header;
